mapper.select:
    	SELECT count(*) FROM reserver

			AND non_name = #{name}
			AND non_email = #{email}
			AND non_pw = #{password}
			AND u_code IS NULL

    	SELECT * FROM reserver r
    	INNER JOIN reservation r2
    	ON r.rsvr_code = r2.rsvr_code

			AND non_name = #{name}
			AND non_email = #{email}
			AND non_pw = #{password}
			AND u_code IS NULL
